import React from "react";

export default class FetchRandomUser extends React.Component {
  state = {
   loading: true,
   latLon: null,
  };

//https://api.openweathermap.org/data/2.5/weather?q=nashville,tennessee&units=imperial&appid=166a433c57516f51dfab1f7edaed8413

  async componentDidMount() {
    const APIKey = "166a433c57516f51dfab1f7edaed8413";
    const BASEURL = "https://api.openweathermap.org/data/2.5/weather?q=";
    let location = "nashville,tennessee";
    const UNITS = "&units=imperial&appid="
    const url = BASEURL+location+UNITS+APIKey;

    const response = await fetch(url);
    const data = await response.json();
    this.setState({latLon: data.results, loading: false });
    console.log(data.coord)
    //const LAT = JSON.stringify(response.data.coord.lat);
    //const LON = JSON.stringify(response.data.coord.lon);
  }


render() {
    return (
        <div>
        {this.state.loading || !this.state.latLon ? (
            <div>loading...</div>
        ) : (
            <div>
                <div>{this.state}</div>
            </div>
        )}
        </div>
    );
}
}
