import React, { Component } from "react";
import FetchLatLon from "./components/FetchLatLon";
import FetchBands from "./components/FetchBands";
import Input from "./components/Input";


class App extends Component {
  state = {
    location: ["nashville,tennessee"],
    items: [],
    isLoaded: false,  
    }

    componentDidMount() {
      console.log("fetch:", this.props.location)
      const APIKey = "166a433c57516f51dfab1f7edaed8413";
      const BASEURL = "https://api.openweathermap.org/data/2.5/weather?q=";
      let location = {location};
      const UNITS = "&units=imperial&appid="

      const url = BASEURL+this.state.location+UNITS+APIKey;
     fetch(url)
     .then(res => res.json())
     // eslint-disable-next-line
     .then(data => {
         this.setState({
             isLoaded: true,
             items: data,
         })
         console.log(data);
     });

    }

  render() {
    return (
      <div className="App">
        <FetchLatLon />
        <FetchBands />
        <form>
          <Input />
        </form>
      </div>
    );
  }
}

export default App;
