import React, { Component } from "react";


class Input extends Component {
   state={
       value: ''
   }

    updateLocation(e) {
        this.setState({
            location: this.state.value,
        });
    }
    render() {
        console.log("value from input.js" ,this.state.value)
            if (this.props.location) {
                var commentNodes = this.props.location.map(function (city) {
                    return (
                        <div className="input-group">
                        <h1>{ city.name} </h1>
                            <div>inputValue: {this.state.value}</div>
                            <hr/>
                            <div>name= {this.props.location.name}</div>
                        <hr/>
                            <div><label for="location">Location: </label>

                        <div className="form-group">
                            <input className="input-field" type="text" defaultValue="nashville,tennessee" value={this.state.value}
                            onChange= {e => this.setState({value: e.target.value})}/>
                            <button onclick={this.updateLocation.bind(this)} >Submit</button>
                    </div>
            </div>
        </div>
      );
    });
}
    }
}
  export default Input;
