import React, { Component } from "react";
import { BrowserRouter as Router, Route } from 'react-router-dom';
import FetchBands from "./components/FetchBands";
import Input from "./components/Input";
import Location from "./components/Location";
import BandScraper from "./components/BandScraper";
const rp = require('request-promise');
const request = require('request')


class App extends Component {
  state = {
    location: [
      {
        cityState: ['nashville,tennessee'],
        items: [],
        newLoc: [],
        bandData: [],
      }
    ]
  }

  componentDidMount () {

    this.fetchAPI(this.state.location[0].cityState)

  }

fetchAPI = () => {

      console.log("fetch:", this.state.location[0].cityState)
      const APIKey = "166a433c57516f51dfab1f7edaed8413";
      const BASEURL = "https://api.openweathermap.org/data/2.5/weather?q=";
      const UNITS = "&units=imperial&appid="
      const place = this.state.location[0].cityState

      let url = BASEURL+place+UNITS+APIKey;
     fetch(url)
     .then(res => res.json())
     // eslint-disable-next-line
     .then(data => {
         this.setState({
             isLoaded: true,
             items: data,
         })
         console.log("fetch url data:", data);
         console.log("bandData:", )
     });
    }


// Add Input
    addInput = (title) => {
      const newInput = {
        cityState: title,

      }
    this.setState({ location: [newInput] });

    }

    getBandData = () => {

      const cheerio = require('cheerio');
      var axios = require('axios');
      let results = []

      const BASEURL = "https://www.bandsintown.com/?latitude=";
    let bLAT = "36.16";
    let bLON = "-86.77";
    const SORT = "&sort_by_filter=Number+of+RSVPs";
    const url = BASEURL + bLAT + "&" + bLON + SORT;
    var _include_headers = function(body, response, resolveWithFullResponse) {
      return {'headers': response.headers, 'data': body};
    };
    const options = {
      method: 'GET',
      uri: url,
      json: true,
      transform: _include_headers,

    transform: function (body) {
      return request(options)
      .then(function(response) {
        console.log(response.headers);
        console.log(response.data);
        cheerio.load(body)
      });
     }

    };
    rp(options)
    .then(res => res.json)
    .then(function ($) {
      $("div.upcomingEvents-075e0336").each(function (i, element){
        $(element).find("h2.event-5daafce9").each(function (i, element) {
          let bandName = $(element).text()
          results.push({
            bandData: results
          })
        })
      })
      console.log("from getBandData():", results)
    })
    .catch(function (err) {
        // Crawling failed or Cheerio choked...
    });


    }

    render() {

      var { isLoaded } = this.state;
      if (!isLoaded) {
          return <div>Loading...</div>
      }
      else {
          return (
            <Router>
              <div className="App">
              <Route exact path="/" render={props => (
                <React.Fragment>
                <div className="container">
                    <ul>
                      <li>
                          {this.state.items.name}
                        </li>
                      <li>
                          {this.state.items.coord.lat}
                      </li>
                        <li>
                            {this.state.items.coord.lon}
                        </li>
                    </ul>

                      <div className="fetchBands">
                        <FetchBands />
                      </div>
                      <div className="bandscraper">
                      <BandScraper />
                      </div>
                      <div className="inputField">
                        <Input addInput={this.addInput} fetchAPI={this.fetchAPI}/>
                            <input 
                            type="submit" 
                            value="Generate" 
                            className="btn"
                            onClick={()=>this.fetchAPI() + this.getBandData()}
                            style={{flex: '1'}}
        />
                      </div>
                      <div>
                      <Location location={this.state.location} switchState={this.switchState} handleChange={this.handleChange} handleSubmit={this.handleSubmit} value={this.state.value}/>
                      </div>
                    </div>
                    <h3><Logger newLat={this.state.items.coord.lat} newLon={this.state.items.coord.lat}/></h3>
                    </React.Fragment>
              )} />



                  </div>
            </Router>
          );
      };
  }
}

class Logger extends Component {

  componentWillReceiveProps(newProps){
    console.log("componentWillRecieveProps() is Triggered");
    console.log("new props:", newProps);

  }

  render(){
    return this.props.newLat
  }
}


export default App;
