import React, { Component } from "react";



class Input extends Component {
    constructor(props) {
        super(props) ;

        this.state = {
            value,
        }
    }
    updateLocation(event) {
        this.setState({
            location: event.target.value
        });
    }
    render() {
        console.log("input: ", this.props.location)
      return (
        <div className="input-group">
            <div>Location: {this.state.location}</div>
        <hr/>
                <div><label for="location">Location: </label>

                <div className="form-group">
                    <input className="input-field" type="text" defaultValue='nashville,tennessee' value={this.state.location}
                    onChange= {this.updateLocation.bind(this)}/>
                    <button onclick={this.updateLocation.bind(this)} >Submit</button>
            </div>
            </div>
        </div>
      )
    }
}
  export default Input;
