import React, {
    Component
} from "react";
const cheerio = require("cheerio");
const axios = require("axios");

class FetchBands extends Component {

    constructor(props) {
        super(props);
        this.state = {
            results: [],
            isLoaded: false,
        }
    }


    // Making a request via axios for reddit's "webdev" board. We are sure to use old.reddit due to changes in HTML structure for the new reddit. The page's Response is passed as our promise argument.
    //axios.get("https://www.bandsintown.com/?place_id=ChIJzWXFYYuifDUR64Pq5LTtioU&sort_by_filter=Number+of+RSVPs").then(function(response) {

    componentDidMount() {
        axios.get("http://localhost:3001/articles:lat=34355")
            .then(function (response) {
                // Log the results once you've looped through each of the elements found with cheerio
                console.log("local api", response);
            });
    }


    render() {
        var {
            isLoaded,
            results
        } = this.state;
        if (!isLoaded) {
            return <div > Loading... </div>
        } else {
            return ( 
            <div className = "fetchBands" >
                <ul>
                    <li> here are results </li>
                </ul>
            </div>
            );
        };
    }
}

export default FetchBands;
