import React from "react";

export default class FetchRandomUser extends React.Component {
  state = {
   // loading: true,
   // person: null
  };

  async componentDidMount() {
    const APIKey = "166a433c57516f51dfab1f7edaed8413";
    const BASEURL = "https://api.openweathermap.org/data/2.5/weather?q=";
    let location = "nashville,tennessee";
    const UNITS = "&units=imperial&appid="
    const url = BASEURL+location+UNITS+APIKey;
    const response = await fetch(url);
    const data = await response.json();
    //const LAT = JSON.stringify(response.data.coord.lat);
    //const LON = JSON.stringify(response.data.coord.lon);
    this.setState({ person: data.results[0], loading: false });
  }

  render() {

    return (
      <div>
        <div>{this.state.coor.name.title}</div>
        <div>{this.state.person.name.first}</div>
        <div>{this.state.person.name.last}</div>
        <div>{this.state.person.location.state}</div>
        <img alt="img" src={this.state.person.picture.large} />

      </div>
    );
  }
}
