import React, { Component } from "react";



class Input extends Component {
   state={
       value: ''
   }

    updateLocation(event) {
        this.setState({
            value: this.props.value,
        });
    }
    render() {
        console.log("state prop from input.js" ,this.props.location)

      return (
        <div className="input-group">
            <div>inputValue: {this.state.value}</div>
            <hr/>
            <div>cityState= {this.props.location}</div>
        <hr/>
                <div><label for="location">Location: </label>

                <div className="form-group">
                    <input className="input-field" type="text" defaultValue="nashville,tennessee" value={this.state.value}
                    onChange= {e => this.setState({value: e.target.value})}/>
                    <button onclick={this.updateLocation.bind(this)} >Submit</button>
            </div>
            </div>
        </div>
      )
    }
}
  export default Input;

  export class FetchLatLon extends Component {

    state = {
            items: [],
            isLoaded: false,
        }


//https://api.openweathermap.org/data/2.5/weather?q=nashville,tennessee&units=imperial&appid=166a433c57516f51dfab1f7edaed8413



componentDidMount() {
    console.log("fetch:", this.props.location)
    const APIKey = "166a433c57516f51dfab1f7edaed8413";
    const BASEURL = "https://api.openweathermap.org/data/2.5/weather?q=";
    //let location = {location};
    const UNITS = "&units=imperial&appid="

    const url = BASEURL+this.props.location+UNITS+APIKey;
   fetch(url)
   .then(res => res.json())
   // eslint-disable-next-line
   .then(data => {
       this.setState({
           isLoaded: true,
           items: data,
       })
       console.log(data);
   });

  }


render() {
    console.log("from fetchLatLon", this.props.location)

    var { isLoaded } = this.state;
    if (!isLoaded) {
        return <div>Loading...</div>
    }
    else {
        return (
            <div className="fetchLatLon">
                <ul>
                        { <li>
                            {this.state.value}
                        </li>
                        }

                </ul>

            </div>
        );
    };
}
}
